import React from "react";
import { Segment, Grid, Divider, Image } from "semantic-ui-react";
import image from "../assets/logo.PNG";

class About extends React.Component {
  render() {
    return (
      <Grid columns={2} relaxed="very" style={{ height: "25vh" }}>
        <Grid.Column verticalAlign="middle" textAlign="center" width={5}>
          <Grid.Row centered>
            <Image src={image} circular />
          </Grid.Row>
          <Grid.Row>
            <h2 style={{ height: "15vh", marginLeft: "33%" }}> About Me </h2>
          </Grid.Row>
        </Grid.Column>
        <Grid.Column verticalAlign="middle" textAlign="left" width={11}>
          <p>
            We can give your company superpowers to do things that they never
            thought possible. Let us delight your customers and empower your
            needs...through pure data analytics. Yes that's right, you thought
            it was the stuff of dreams, but even bananas can be bioengineered.{" "}
          </p>
        </Grid.Column>
      </Grid>
    );
  }
}

export default About;
